import { useEffect, useState } from 'react'
import io from 'socket.io-client'
import TreeChannel from './components/TreeChannel/TreeChannel'

import classes from './Admin.module.css'
import ModalWindows from './components/UI/ModalMindow/ModalWindow'
import SendChatForm from '../../Form/SendChatForm'

const socket = io('http://10.147.18.205:3001')

const Admin = () => {
	const [AllChats, setAllChats] = useState(null)
	useEffect(() => {
		const fetchData = async () => {
			try {
				socket.emit('GetAllChannelFromServer', results => {
					if (results !== null)
						results.categorys = new Map(JSON.parse(results.categorys))
					setAllChats(results)
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}

		fetchData()
		return () => {
			socket.off('updatePlaylist')
		}
	}, [])

	const SubmitItemHandler = ({ name, type, id,item }) => {
		if(type==='text')
			socket.emit('sendToChat',{id:id,text:item})
	}

	return (
		<div className={classes.container}>
			<TreeChannel nodeChats={AllChats} callback={SubmitItemHandler} />

		</div>
	)
}

export default Admin
