import { useState } from 'react'
import {
	BrowserRouter as Router,
	Routes,
	Route,
	useLocation,
	Switch,
} from 'react-router-dom'


const Menu = ({ items, onItemClick }) => {
	const [selectedItem, setSelectedItem] = useState(null)

	const handleItemClick = item => {
		setSelectedItem(item)
		onItemClick(item)
	}

	return (
		<div>
			{items.map(item => (
				<MenuItem
					key={item.id}
					item={item}
					onClick={() => handleItemClick(item)}
					isSelected={selectedItem && selectedItem.id === item.id}
				/>
			))}
		</div>
	)
}

const MenuItem = ({ item, onClick, isSelected }) => {
	return (
		<div
			onClick={onClick}
			style={{
				cursor: 'pointer',
				padding: '10px',
				margin: '5px',
				background: isSelected ? '#eee' : 'transparent',
			}}
		>
			{item.name}
		</div>
	)
}
//tested

const MainPage = () => {
	const [selecetedId, setId] = useState(-1)
	const location = useLocation()
	const ListServers = location.state || []
	const handleItemClick = selectedItem => {
		setId(selectedItem.id)
	}
	console.log(ListServers)

	return (
		<div>
			<Menu items={ListServers} onItemClick={handleItemClick} />
			<TopMenu currentid={selecetedId} listServer={ListServers} />

		</div>
	)
}

export default MainPage
