import { useState } from 'react'
import {
	BrowserRouter as Router,
	Routes,
	Route,
	useLocation,
} from 'react-router-dom'
import TopMenu from '@component/TopMenu/TopMenu'
import io from 'socket.io-client'
import { SocketProvider } from '../Context/SocketContext'

const Menu = ({ items, onItemClick }) => {
	const [selectedItem, setSelectedItem] = useState(null)

	const handleItemClick = item => {
		setSelectedItem(item)
		onItemClick(item)
	}
	console.log(items)
	return (
		<div>
			{items.map(item => (
				<MenuItem
					key={item.id}
					item={item}
					onClick={() => handleItemClick(item)}
					isSelected={selectedItem && selectedItem.id === item.id}
				/>
			))}
		</div>
	)
}

const MenuItem = ({ item, onClick, isSelected }) => {
	return (
		<div
			onClick={onClick}
			style={{
				cursor: 'pointer',
				padding: '10px',
				margin: '5px',
				background: isSelected ? '#eee' : 'transparent',
			}}
		>
			{item.name}
		</div>
	)
}
//tested

const MainPage = () => {
	const [selecetedId, setId] = useState(-1)
	const location = useLocation()

	const [ListServers, setList] = useState(location.state.list || [])
	const [User, seUser] = useState(location.state.user || {})
	const handleItemClick = selectedItem => {
		setId(selectedItem.id)
	}
	const socket = io('http://localhost:3000', {
		query: { userId: User.id },
	})
	console.log(ListServers)

	return (
		<div>
			<SocketProvider socket={socket}>
				<Menu items={ListServers} onItemClick={handleItemClick} />
				<TopMenu
					userId={User.id}
					currentid={selecetedId}
					listServer={ListServers}
				/>
				<Routes></Routes>
			</SocketProvider>
		</div>
	)
}

export default MainPage
