import classes from './App.module.css'
import { useEffect, useState } from 'react'
import PlayedList from './component/PlayedList'
import io from 'socket.io-client'

function App() {
	const [songs, setSongs] = useState([])

	useEffect(() => {

		socket.on('updatePlaylist', ({ action, song }) => {
			if (song === undefined) return
			if (action === 'allItems') {
				console.log('allSongs:', song)
				setSongs(song)
				// Обновите свой локальный стейт с новой песней
			} else if (action === 'add') {
				setSongs([...songs, song])
				// Обновите свой локальный стейт с новой песней
			} else if (action === 'remove') {
				console.log('Song removed:', song)
				let indexToRemove = songs.findIndex(obj => obj.url === song)
				console.log(indexToRemove)
				if (indexToRemove !== -1) {
					setSongs(songs.splice(indexToRemove, 1))
				}
			}
		})

		return () => {
			// Отключение обработчика при размонтировании компонента
			socket.disconnect()
		}
		// Вызываем функцию при монтировании компонента
	}, [songs]) // Пустой массив зависимостей гарантирует, что эффект выполняется только один раз при монтировании
	const click = item => {
		console.log(item)
	}

	console.log(songs)
	return (
		<div className={classes.container}>
			<div className={classes.player}>
				{songs.length === 0 ? (
					<p></p>
				) : (
					<div className={classes.songTitle}>Now Playing: {songs[0].title}</div>
				)}

				<div className={classes.controls}>
					<button>Previous</button>
					<button>Play</button>
					<button>Next</button>
				</div>
			</div>
			<PlayedList items={songs} click={click} />
		</div>
	)
}

export default App
