import { useState, useEffect } from 'react'
import { useLocation, Outlet, useNavigate, Routes } from 'react-router-dom'

import TopMenu from '@component/TopMenu/TopMenu'

const Menu = ({ items, onItemClick }) => {
	const [selectedItem, setSelectedItem] = useState(null)

	const handleItemClick = item => {
		setSelectedItem(item)
		onItemClick(item)
	}
	console.log(items)
	return (
		<div>
			{items.map(item => (
				<MenuItem
					key={item.id}
					item={item}
					onClick={() => handleItemClick(item)}
					isSelected={selectedItem && selectedItem.id === item.id}
				/>
			))}
		</div>
	)
}

const MenuItem = ({ item, onClick, isSelected }) => {
	return (
		<div
			onClick={onClick}
			style={{
				cursor: 'pointer',
				padding: '10px',
				margin: '5px',
				background: isSelected ? '#eee' : 'transparent',
			}}
		>
			{item.name}
		</div>
	)
}
//tested

const MainPage = () => {
	const [selectedId, setId] = useState(-1)
	const location = useLocation()
	const ListServers = location.state?.list || []
	const User = location.state?.user || undefined
	const navigate = useNavigate()

	const handleItemClick = selectedItem => {
		setId(selectedItem.id)
	}
	useEffect(() => {
		if (User === undefined) {
			navigate('/')
		}
	}, [User, navigate])
	return (
		<div>
			{User !== undefined && (
				<>
					<Menu items={ListServers} onItemClick={handleItemClick} />
					<TopMenu
						userId={User.id}
						currentId={selectedId}
						listServer={ListServers}
					/>
				</>
			)}
			<Routes>
				<Route path='/music' element={<MusicPage />} />
				<Route path='/admin' element={<AdminPage />} />
				<Route path='/menu/contact' element={<HomePage />} />
			</Routes>
		</div>
	)
}

export default MainPage
