import React from 'react'
import classes from './PlayeList.module.css'
import PlayedItem from './PlayedList/PlayedItem/PlayedItem'

const PlayedList = ({ currentPlayed, items, remove, play,onImport }) => {
	const exportPlaylist = () => {
		const jsonData = JSON.stringify(items)

		const blob = new Blob([jsonData], { type: 'application/json' })
		const url = URL.createObjectURL(blob)

		const a = document.createElement('a')
		a.href = url
		a.download = 'Playlist.json'
		document.body.appendChild(a)
		a.click()
		document.body.removeChild(a)
		URL.revokeObjectURL(url)
	}
	 const handleFileChange = e => {
			const file = e.target.files[0]
			if (file) {
				const reader = new FileReader()
				reader.onload = event => {
					try {
						const fileContent = event.target.result
						const parsedData = JSON.parse(fileContent)
						setLoadedData(parsedData)
						// Дополнительные действия с загруженными данными
					} catch (error) {
						console.error('Error parsing JSON:', error)
					}
				}
				reader.readAsText(file)
			}
		}

		const handleClick = () => {
			// Клик по скрытому input элементу, который отвечает за выбор файла
			document.getElementById('fileInput').click()
		}
	return (
		<div className={classes.playlistContainer}>
			<ul className={classes.playlist}>
				{items.length === 0 ? (
					<h1>Нет аудио для проигрыванния</h1>
				) : (
					items.map(item => (
						<PlayedItem
							index={item.id}
							playlistItem={item}
							currentPlayed={currentPlayed}
							remove={remove}
							play={play}
						/>
					))
				)}
			</ul>
			<div>
				<button onClick={() => exportPlaylist()}>Export Playlist</button>
				<button>Import Playlist</button>
				<input
					id='fileInput'
					type='file'
					style={{ display: 'none' }}
					onChange={handleFileChange}
				/>
			</div>
		</div>
	)
}

export default PlayedList
