import { useState, useEffect } from 'react'
import { useNavigate, useLocation, Outlet } from 'react-router-dom'
import classes from './MainPage.module.css'
import TopMenu from '@component/TopMenu/TopMenu'

import { Nav } from 'react-bootstrap'

const Menu = ({ items, onItemClick }) => {
	const [selectedItem, setSelectedItem] = useState(null)

	const handleItemClick = item => {
		setSelectedItem(item)
		onItemClick(item)
	}

	return (
		<Nav className='justify-content-center' variant='tabs'>
			{items.map(item => (
				<MenuItem
					key={item.id}
					item={item}
					onClick={() => handleItemClick(item)}
					isSelected={selectedItem && selectedItem.id === item.id}
				/>
			))}
		</Nav>
	)
}

const MenuItem = ({ item, onClick, isSelected }) => {
	return (
		<Nav.Item>
			<Nav.Link
				onClick={onClick}
				active={isSelected}
				style={{
					cursor: 'pointer',
					backgroundColor: isSelected ? '#eee' : 'transparent',
				}}
			>
				{item.name}
			</Nav.Link>
		</Nav.Item>
	)
}

//tested

const MainPage = () => {
	const [selectedId, setId] = useState(-1)
	///	const ListServers = location.state?.list || []
	//const User = location.state?.user || undefined
	const [ListServers, setListServers] = useState(undefined)
	const [User, setUser] = useState(location.state?.user || undefined)
	const authenticateUser = userData => {
		localStorage.setItem('userData', JSON.stringify(userData))
	}
	const getUserData = () => {
		const storedUserData = localStorage.getItem('userData')
		return storedUserData ? JSON.parse(storedUserData) : null
	}
	const isUserAuthenticated = () => {
		return getUserData() !== null
	}
	const logoutUser = () => {
		localStorage.removeItem('userData')
	}

	if (User === undefined && isUserAuthenticated()) {
		const data = getUserData()
		setListServers(data.ListServers)
		setUser(data.User)
	} else {
		authenticateUser({ ListServers, User })
	}

	const navigate = useNavigate()

	const handleItemClick = selectedItem => {
		console.log('clicked', selectedItem.id)
		setId(selectedItem.id)
		console.log('clicked', selectedId)
	}
	useEffect(() => {
			const location = useLocation()
setListServers()
		if (User === undefined) {
			navigate('/')
		}
	}, [User, navigate])
	return (
		<div className={classes.container}>
			{User !== undefined && (
				<>
					<TopMenu
						userId={User.id}
						currentid={selectedId}
						listServer={ListServers}
					/>
					<Menu items={ListServers} onItemClick={handleItemClick} />
				</>
			)}
			<Outlet />
		</div>
	)
}

export default MainPage
