import classes from './Music.module.css'
import { useEffect, useState } from 'react'
import PlayedList from './PlayedList/PlayedList'
import io from 'socket.io-client'
import MusicPlayer from './MusicPlayer/MusicPlayer'

const socket = io('http://10.147.18.205:3002')

function App() {
	const [songs, setSongs] = useState([])
	const [currentPlayed, setCurrentPlayed] = useState(-1)

	useEffect(() => {
		const fetchData = async () => {
			try {
				socket.emit('getAllInfo')
				socket.on('updatePlaylist', ({ action, song }) => {
					if (song === undefined) return
					if (action === 'allItems') {
						setSongs(song)

						socket.emit('getCurrentPlayed', callback => {
							setCurrentPlayed(callback)
						})
					} else if (action === 'newCurrentPlayed') {
						setCurrentPlayed(song)
					} else if (action === 'add') {
						setSongs(songs => [...songs, song])
					} else if (action === 'remove') {
						setSongs(songs => {
							const updatedSongs = [...songs]

							if (song !== -1) {
								updatedSongs.splice(song, 1)
							}

							return updatedSongs
						})
					}
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}

		fetchData()
		return () => {
			socket.off('updatePlaylist')
		}
	}, [songs, currentPlayed])

	const play = item => {
		socket.emit('setPlayItem', { item })
	}
	const remove = item => {
		socket.emit('deltetePlayItem', { item })
	}
	const handlerImport = obj => {
		console.log(obj)
		socket.emit('importedPlaylist', obj)
	}
	const finded = songs.find(x => x.id === currentPlayed)
	const text =
		currentPlayed === -1 ? '' : finded === undefined ? '' : finded.title
	return (
		<div className={classes.container}>
			<div className={classes.info}>

				{finded === undefined ? (
					<p></p>
				) : (
					<div>
						<h1>{text}</h1>
						<p>Песню установил: {finded.customer}</p>
						<p>Сылка на песню: {finded.url}</p>
					</div>
				)}
			</div>

			<PlayedList
				remove={remove}
				play={play}
				onImport={handlerImport}
				currentPlayed={currentPlayed}
				items={songs}
			/>
		</div>
	)
}

export default App
