					} else if (action === 'remove') {
						console.log('Song removed:', song)
						// Обновите свой локальный стейт, удалив песню с определенным ID
					}
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}


		// Вызываем функцию при монтировании компонента
		fetchData()
	}, []) // Пустой массив зависимостей гарантирует, что эффект выполняется только один раз при монтировании
