import { useState, useEffect } from 'react'
import { useLocation, Outlet } from 'react-router-dom'

import TopMenu from '@component/TopMenu/TopMenu'

const Menu = ({ items, onItemClick }) => {
	const [selectedItem, setSelectedItem] = useState(null)

	const handleItemClick = item => {
		setSelectedItem(item)
		onItemClick(item)
	}
	console.log(items)
	return (
		<div>
			{items.map(item => (
				<MenuItem
					key={item.id}
					item={item}
					onClick={() => handleItemClick(item)}
					isSelected={selectedItem && selectedItem.id === item.id}
				/>
			))}
		</div>
	)
}

const MenuItem = ({ item, onClick, isSelected }) => {
	return (
		<div
			onClick={onClick}
			style={{
				cursor: 'pointer',
				padding: '10px',
				margin: '5px',
				background: isSelected ? '#eee' : 'transparent',
			}}
		>
			{item.name}
		</div>
	)
}
//tested

const MainPage = () => {
	const [selecetedId, setId] = useState(-1)
	const location = useLocation()
	const ListServers = useState(location.state.list || [])
	const [User, seUser] = useState(location.state.user || {})
	  const navigate = useNavigate()

	const handleItemClick = selectedItem => {
		setId(selectedItem.id)
	}
	useEffect(() => {
		// Переходить на /auntification, если User не определен (или другая логика перехода)
		if (!User.id) {
			navigate('/auntification')
		}
	}, [User, navigate])
	return (
		<div>
			<Menu items={ListServers} onItemClick={handleItemClick} />
			<TopMenu
				userId={User.id}
				currentid={selecetedId}
				listServer={ListServers}
			/>
			<Outlet />
		</div>
	)
}

export default MainPage
