import classes from './Music.module.css'
import {useState, useEffect } from 'react'
import PlayedList from './PlayedList/PlayedList'
import { useLocation } from 'react-router-dom'


import MusicPlayer from './MusicPlayer/MusicPlayer'
import { useUpdatePlaylist } from './hooks/useUpdatePlaylist'
import io from 'socket.io-client'
const socket = io('http://localhost:3004')
function Music() {
	const location = useLocation()
const [playedStatus, setStatus] = useState(true)
	const idServer = location.state.idServer || undefined
	const idUser = location.state.idUser || undefined
	const { songs, currentPlayed } = useUpdatePlaylist(socket, idServer, idUser)

	//const [socket, setSocket] = useState(null)
	//const [socket, setSocket] = useState(io('http://localhost:3006'))
	useEffect(() => {
		const fetchData = async () => {
			try {
				socket.emit(
					'connectMe',
					{ idServer: serverId, idUser: idUser },
					callback => {
						console.log(callback)
					}
				)

				socket?.emit('getAllInfo', { id: serverId })
				socket?.on('updatePlaylist', ({ action, song }) => {
					if (song === undefined) return
					if (action === 'allItems') {
						setPlalistInfo({
							...plalistInfo,
							songs: song,
						})
						console.log('set new playlist', song)

						socket?.emit('getCurrentPlayed', { id: serverId }, callback => {
							setPlalistInfo({
								...plalistInfo,
								currentPlayed: callback,
							})
						})
					} else if (action === 'newCurrentPlayed') {
						setPlalistInfo({
							...plalistInfo,
							currentPlayed: song,
						})
					} else if (action === 'add') {
						setPlalistInfo({
							...plalistInfo,
							songs: [...plalistInfo.songs, song],
						})
					} else if (action === 'remove') {
						const remove = songs => {
							const updatedSongs = [...songs]

							if (song !== -1) {
								updatedSongs.splice(song, 1)
							}

							return updatedSongs
						}
						setPlalistInfo({
							...plalistInfo,
							songs: remove(plalistInfo.songs),
						})
					}
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}

		fetchData()
		return () => {
			socket?.off('updatePlaylist')
		}
	}, [idServer])


	const play = item => {
		console.log('PLAYED')
		console.log(item)
		socket?.emit('setPlayItem', { id: idServer, item: item })
	}
	const remove = item => {
		socket?.emit('deltetePlayItem', { id: idServer, item: item })
	}
	const handlerImport = obj => {
		socket?.emit('importedPlaylist', { id: idServer, obj: obj })
	}
	console.log('SIZE NEW ARRAY   ', songs.length)
	const finded = songs.find(x => x.id === currentPlayed)
	const text =
		currentPlayed === -1 ? '' : finded === undefined ? '' : finded.title
	return (
		<div className={classes.container}>
			<div className={classes.info}>
				{finded === undefined ? (
					<h1>Не чего не играет =)</h1>
				) : (
					<div>
						<h1>{text}</h1>
						<p>Песню установил: {finded.сustomer}</p>
						<p>Сылка на песню: {finded.url}</p>
					</div>
				)}
				<MusicPlayer id={idServer} text={text} socket={socket} />
			</div>
			{songs.length === 0 ? (
				<h1>Нет аудио для проигрыванния</h1>
			) : (
				<PlayedList
					remove={remove}
					play={play}
					onImport={handlerImport}
					currentPlayed={currentPlayed}
					items={songs}
				/>
			)}
		</div>
	)
}

export default Music
