import SendChatForm from '../../../../../Form/SendChatForm'
import ModalWindows from '../../../../../UI/ModalMindow/ModalWindow'
import { useState } from 'react'

const TreeItem = ({ item, SubmitTextCallback }) => {
	const [visibleModal, setVisibleModal] = useState(false)

	let icon = ''
	switch (item.type) {
		case 'text':
			icon = '📝'
			break
		case 'voice':
			icon = '🔈'
			break
		default:
			icon = '🛑'
			break
	}
	const ClickCallback = () => {
		if (item.type === 'text') {
			setVisibleModal(true)
		}
	}
	const ChatFormHandler = text => {
		SubmitTextCallback({ ...item, item: text })
	}
	return (
		<li className='list-group-item'>
			<div>
				<p onClick={() => ClickCallback(item)}>
					{item.name} {icon}
				</p>
			</div>
			<ModalWindows text='отправить в' visible={visibleModal} setVisible={setVisibleModal}>
				<SendChatForm onSubmitText={ChatFormHandler} />
			</ModalWindows>
		</li>
	)
}

export default TreeItem
