import classes from './App.module.css'
import { useEffect, useState } from 'react'
import PlayedList from './component/PlayedList'
import io from 'socket.io-client'

const socket = io('http://10.147.18.205:3001')

function App() {
	const [songs, setSongs] = useState([])
	const [currentPlayed, setCurrentPlayed] = useState(-1)
	const [currentStream, setCurrentStream] = useState(null)
	useEffect(() => {
		const getStream = () => {
			socket.emit('getStream', stream => {
				if (stream.stream) {
					console.log(stream)
					setCurrentStream(stream.stream)
				}
			})
		}
		// Функция для получения данных с бэкенда
		const fetchData = async () => {
			try {
				socket.on('updatePlaylist', ({ action, song }) => {
					if (song === undefined)
						if (action === 'allItems') {
							console.log('allSongs:', song)
							setSongs(song)

							socket.emit('getCurrentPlayed', { currentPlayed }, index => {
								setCurrentPlayed(index.currentPlayed)
							})
						} else if (action === 'add') {
							setSongs([...songs, song])
						} else if (action === 'remove') {
							setSongs(songs.slice(1))
						} else if (action === 'newCurrentPlayed') {
							setCurrentPlayed(song)
						}
					getStream()
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}

		// Вызываем функцию при монтировании компонента
		fetchData()
		return () => {
			socket.off('updatePlaylist')
		}
	}, [songs, currentPlayed]) // Пустой массив зависимостей гарантирует, что эффект выполняется только один раз при монтировании
	const click = item => {
		console.log(item)
	}

	const finded = songs.find(x => x.id === currentPlayed)
	const text =
		currentPlayed === -1 ? '' : finded === undefined ? '' : finded.title
	console.log('text: ', currentPlayed)
	return (
		<div className={classes.container}>
			<div className={classes.player}>
				<div className={classes.songTitle}>Now Playing: {text}</div>
				{currentStream === null ? (
					<h1>No playing songs</h1>
				) : (
					<audio>
						<source src={currentStream} type='audio/mp3'></source>
					</audio>
				)}
				<div className={classes.controls}>
					<button>Previous</button>
					<button>Play</button>
					<button>Next</button>
				</div>
			</div>
			<PlayedList currentPlayed={currentPlayed} items={songs} click={click} />
		</div>
	)
}

export default App
