import { useState, useEffect } from 'react'
import { useNavigate, useLocation, Outlet, Link } from 'react-router-dom'
import classes from './MainPage.module.css'
import TopMenu from '@component/TopMenu/TopMenu'
import logo from '@image/logo.png'




const MainPage = () => {
	const [selectedId, setId] = useState(-1)
	const location = useLocation()
	///	const ListServers = location.state?.list || []
	//const User = location.state?.user || undefined
	const [ListServers, setListServers] = useState(undefined)
	const [User, setUser] = useState(undefined)
	const authenticateUser = userData => {
		localStorage.setItem('userData', JSON.stringify(userData))
	}
	const getUserData = () => {
		const storedUserData = localStorage.getItem('userData')
		if (storedUserData !== undefined)
			return storedUserData ? JSON.parse(storedUserData) : null
	}
	const isUserAuthenticated = () => {
		return getUserData() !== null
	}

	const navigate = useNavigate()

	const handleItemClick = selectedItem => {
		setId(selectedItem.id)
	}
	useEffect(() => {
		const locationState = location.state
		const storedUserData = getUserData()
		if (locationState !== null) {
			console.log('USED STATE', locationState.user)
			setListServers(locationState.list || [])
			setUser(locationState.user || undefined)
			authenticateUser({
				User: locationState.user,
				ListServers: locationState.list,
			})
		} else if (storedUserData?.User !== undefined) {
			setListServers(storedUserData.ListServers)
			setUser(storedUserData.User)
			console.log()
		} else {
			navigate('/')
		}
	}, [])

	const ItemMenuCenterItem = [
		{
			text: 'Home',
			to: '/menu',
			state: { ListServers },
		},
		{
			text: 'Music',
			to: '/menu/music',
			state: { idServer: selectedId, idUser: User?.id },
		},
		{
			text: 'Admin',
			to: '/menu/admin',
			state: { idServer: selectedId, idUser: User?.id },
		},
		{
			text: 'Contact',
			to: '/menu/contact',
		},
	]

	const LogoItem = (
		<Link className='navbar-brand' to='/'>
			<img src={logo} className={classes.logo} alt='site logo'></img>
			Finer
		</Link>
	)
	const ItemMenuLastItem = [
		{
			text: 'Logout',
			to: '/',
			callback: e => {
				e.preventDefault()
				localStorage.removeItem('userData')
				navigate('/')
			},
		},
	]
	const PATH_TO_IMG = `https://cdn.discordapp.com/avatars/${User?.id}/${User?.avatar}.png`
	const LastItemMenu = (
		<Link className='nav-link' state={User} to='/menu/me'>
			<img src={PATH_TO_IMG} alt='user avatar' className={classes.avatar} />
		</Link>
	)
	return (
		<div className={classes.container}>
			{User !== undefined && (
				<>
					<TopMenu
						user={User}
						currentid={selectedId}
						listServer={ListServers}
						NavigationItemCenter={ItemMenuCenterItem}
						LogoItem={LogoItem}
						RightItemMenu={ItemMenuLastItem}
						LastItemMenu={LastItemMenu}
					/>
					<Menu items={ListServers} onItemClick={handleItemClick} />
				</>
			)}
			<Outlet />
		</div>
	)
}

export default MainPage
