import classes from './Music.module.css'
import { useState, useEffect } from 'react'
import PlayedList from './PlayedList/PlayedList'
import { Routes, useLocation } from 'react-router-dom'

import MusicPlayer from './MusicPlayer/MusicPlayer'
import { useUpdatePlaylist } from './hooks/useUpdatePlaylist'
import io from 'socket.io-client'
const socket = io('http://localhost:3004')
function Music() {
	const location = useLocation()
	const [playedStatus, setStatus] = useState(true)
	const [plalistInfo, setPlalistInfo] = useState({
		songs: [],
		currentPlayed: -1,
	})
	console.log(location.state)
	const idServer = location.state.idServer || undefined
	const idUser = location.state.idUser || undefined
	//const { songs, currentPlayed } = useUpdatePlaylist(socket, idServer, idUser)
	const navigate = use
	useEffect(() => {
		const fetchData = async () => {
			try {
				if(idServer === undefined){
					navigate('/menu', {
						state: response.data,
					})
				}

				socket.emit(
					'connectMe',
					{ idServer: idServer, idUser: idUser },
					callback => {
						console.log(callback)
					}
				)

				socket?.on('updatePlayed', status => {
					setStatus(status)
				})

				socket?.on('updatePlaylist', ({ action, song }) => {
					if (song === undefined) return
					if (action === 'allItems') {
						socket?.emit('getCurrentStatus', { id: idServer }, callback => {
							setStatus(callback)
						})
					}
				})
				socket?.emit('getAllInfo', { id: idServer })
				socket?.on('updatePlaylist', ({ action, song }) => {
					if (song === undefined) return
					if (action === 'allItems') {
						setPlalistInfo(prevInfo => ({
							...prevInfo,
							songs: song,
						}))
						console.log('set new playlist', song)

						socket?.emit('getCurrentPlayed', { id: idServer }, callback => {
							setPlalistInfo(prevInfo => ({
								...prevInfo,
								currentPlayed: callback,
							}))
						})
					} else if (action === 'newCurrentPlayed') {
						setPlalistInfo(prevInfo => ({
							...prevInfo,
							currentPlayed: song,
						}))
					} else if (action === 'add') {
						setPlalistInfo(prevInfo => ({
							...prevInfo,
							songs: [...prevInfo.songs, song],
						}))
					} else if (action === 'remove') {
						const remove = songs => {
							const updatedSongs = [...songs]

							if (song !== -1) {
								updatedSongs.splice(song, 1)
							}

							return updatedSongs
						}
						setPlalistInfo(prevInfo => ({
							...prevInfo,
							songs: remove(prevInfo.songs),
						}))
					}
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}

		fetchData()
		return () => {
			socket?.off('updatePlaylist')
			socket?.off('updatePlaylist')
			socket?.off('updatePlayed')
			socket?.close()
		}
	}, [idServer])

	const play = item => {
		console.log('PLAYED')
		console.log(item)
		socket?.emit('setPlayItem', { id: idServer, item: item })
	}
	const remove = item => {
		socket?.emit('deltetePlayItem', { id: idServer, item: item })
	}
	const handlerImport = obj => {
		socket?.emit('importedPlaylist', { id: idServer, obj: obj })
	}
	console.log('SIZE NEW ARRAY   ', plalistInfo.songs.length)
	const finded = plalistInfo.songs.find(x => x.id === plalistInfo.currentPlayed)
	const text =
		plalistInfo.currentPlayed === -1
			? ''
			: finded === undefined
			? ''
			: finded.title
	return (
		<div className={classes.container}>
			<div className={classes.info}>
				{finded === undefined ? (
					<h1>Не чего не играет =)</h1>
				) : (
					<div>
						<h1>{text}</h1>
						<p>Песню установил: {finded.сustomer}</p>
						<p>Сылка на песню: {finded.url}</p>
					</div>
				)}
				<MusicPlayer
					playedStatus={playedStatus}
					id={idServer}
					text={text}
					socket={socket}
				/>
			</div>
			{plalistInfo.songs.length === 0 ? (
				<h1>Нет аудио для проигрыванния</h1>
			) : (
				<PlayedList
					remove={remove}
					play={play}
					onImport={handlerImport}
					currentPlayed={plalistInfo.currentPlayed}
					items={plalistInfo.songs}
				/>
			)}
		</div>
	)
}

export default Music
