import classes from './Music.module.css'
import React from 'react'
import PlayedList from './PlayedList/PlayedList'
import { useLocation } from 'react-router-dom'

import MusicPlayer from './MusicPlayer/MusicPlayer'
import { useUpdatePlaylist } from './hooks/useUpdatePlaylist'
import io from 'socket.io-client'

function Music() {
	console.log()
	const location = useLocation()
	const socket = io('http://localhost:3000', {
		query: { serverId },
	})
	console.log('SOCKET: ', socket)
	const idServer = location.state.idServer || undefined
	const idUser = location.state.idUser || undefined
	const { songs, currentPlayed } = useUpdatePlaylist(socket, idServer)
useEffect(() => {
	// Подключение к серверу с указанием параметра serverId
	const socket = io('http://localhost:3000', {
		query: { serverId },
	})

	// Отправка событий на сервер
	socket.emit('someEvent', 'Hello, server!')

	// Обработка событий от сервера
	socket.on('someEventResponse', data => {
		console.log(`Received response from server: ${data}`)
	})

	// Обработка отключения
	return () => {
		console.log('Disconnecting from server')
		socket.disconnect()
	}
}, [serverId])
	const play = item => {
		socket.emit('setPlayItem', { id: idServer, item: item })
	}
	const remove = item => {
		socket.emit('deltetePlayItem', { id: idServer, item: item })
	}
	const handlerImport = obj => {
		socket.emit('importedPlaylist', { id: idServer, obj: obj })
	}
	const finded = songs.find(x => x.id === currentPlayed)
	const text =
		currentPlayed === -1 ? '' : finded === undefined ? '' : finded.title
	return (
		<div className={classes.container}>
			<div className={classes.info}>
				{finded === undefined ? (
					<h1>Не чего не играет =)</h1>
				) : (
					<div>
						<h1>{text}</h1>
						<p>Песню установил: {finded.сustomer}</p>
						<p>Сылка на песню: {finded.url}</p>
					</div>
				)}
				<MusicPlayer text={text} socket={socket} />
			</div>
			{songs.length === 0 ? (
				<h1>Нет аудио для проигрыванния</h1>
			) : (
				<PlayedList
					remove={remove}
					play={play}
					onImport={handlerImport}
					currentPlayed={currentPlayed}
					items={songs}
				/>
			)}
		</div>
	)
}

export default Music
