import classes from './Music.module.css'
import { useEffect, useState } from 'react'
import PlayedList from './PlayedList/PlayedList'
import io from 'socket.io-client'

const socket = io('http://10.147.18.205:3002')

function App() {
	const [songs, setSongs] = useState([])
	const [currentPlayed, setCurrentPlayed] = useState(-1)
	const [playedStatus, setStatus] = useState(true)

	useEffect(() => {
		const fetchData = async () => {
			try {
				socket.on('updatePlayed', status => {
					setStatus(status)
				})
				socket.on('updatePlaylist', ({ action, song }) => {
					if (song === undefined) return
					if (action === 'allItems') {
						setSongs(song)

						socket.emit('getCurrentStatus', callback => {
							setStatus(callback)
						})
						socket.emit('getCurrentPlayed', { currentPlayed }, index => {
							setCurrentPlayed(index)
						})
					} else if (action === 'add') {
						setSongs(songs => [...songs, song])
					} else if (action === 'remove') {
						setSongs(songs => {
							const updatedSongs = [...songs]

							if (song !== -1) {
								updatedSongs.splice(song, 1)
							}

							return updatedSongs
						})
					} else if (action === 'newCurrentPlayed') {
						setCurrentPlayed(song)
					}
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}

		fetchData()
		return () => {
			socket.off('updatePlaylist')
		}
	}, [songs, currentPlayed])

	const play = item => {
		socket.emit('setPlayItem', { item })
	}
	const remove = item => {
		socket.emit('deltetePlayItem', { item })
	}
	const handlerImport = obj => {
		console.log(obj)
		socket.emit('importedPlaylist', obj)
	}
	const changePlayedStatuc = () => {
		socket.emit('changeMusicStatus', !playedStatus)
	}
	return (
		<div className={classes.container}>

			<PlayedList
				remove={remove}
				play={play}
				onImport={handlerImport}
				currentPlayed={currentPlayed}
				items={songs}
			/>
		</div>
	)
}

export default App
