import { useState, useEffect } from 'react'
import classes from './MusicPlayer.module.css'

const MusicPlayer = ({ songs, socket }) => {
	const [playedStatus, setStatus] = useState(true)

	const changePlayedStatuc = () => {
		socket.emit('changeMusicStatus', !playedStatus)
	}
	const goNextSongs = () => {
		socket.emit('goNextSongs')
	}
	const goPrevSongs = () => {
		socket.emit('goPrevSongs')
	}

	useEffect(() => {
		const fetchData = async () => {
			try {
				socket.on('updatePlayed', status => {
					setStatus(status)
				})
				socket.on('updatePlaylist', ({ action, song }) => {
					if (song === undefined) return
					if (action === 'allItems') {
						socket.emit('getCurrentStatus', callback => {
							setStatus(callback)
						})
					}
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}

		fetchData()
		return () => {
			socket.off('updatePlaylist')
		}
	}, [playedStatus])



	return (
		<div className={classes.player}>
			{text === '' ? (
				<p></p>
			) : (
				<div className={classes.songTitle}>Now Playing: {text}</div>
			)}

			<div className={classes.controls}>
				<button onClick={() => goPrevSongs()}>Previous</button>
				<button onClick={() => changePlayedStatuc()}>
					{playedStatus ? 'Pause' : 'Resume'}
				</button>
				<button onClick={() => goNextSongs()}>Next</button>
			</div>
		</div>
	)
}

export default MusicPlayer
