import { useState, useEffect } from 'react'
import {
	BrowserRouter as Router,
	Routes,
	Route,
	useNavigate,
	useLocation,
	Outlet,

} from 'react-router-dom'
import MusicPage from '@component/Pages/MusicPage/Music.jsx'
import HomePage from '@component/Pages/HomePage/Home'
import AdminPage from '@component/Pages/AdminPage/Admin'
import TopMenu from '@component/TopMenu/TopMenu'

const Menu = ({ items, onItemClick }) => {
	const [selectedItem, setSelectedItem] = useState(null)

	const handleItemClick = item => {
		setSelectedItem(item)
		onItemClick(item)
	}
	console.log(items)
	return (
		<div>
			{items.map(item => (
				<MenuItem
					key={item.id}
					item={item}
					onClick={() => handleItemClick(item)}
					isSelected={selectedItem && selectedItem.id === item.id}
				/>
			))}
		</div>
	)
}

const MenuItem = ({ item, onClick, isSelected }) => {
	return (
		<div
			onClick={onClick}
			style={{
				cursor: 'pointer',
				padding: '10px',
				margin: '5px',
				background: isSelected ? '#eee' : 'transparent',
			}}
		>
			{item.name}
		</div>
	)
}
//tested

const MainPage = () => {
	const [selectedId, setId] = useState(-1)
	const location = useLocation()
	const ListServers = location.state?.list || []
	const User = location.state?.user || undefined
	const navigate = useNavigate()

	const handleItemClick = selectedItem => {
		setId(selectedItem.id)
	}

	console.log(User)
	return (
		<div>
			<Menu items={ListServers} onItemClick={handleItemClick} />
			<TopMenu
				userId={User.id}
				currentId={selectedId}
				listServer={ListServers}
			/>
			<Outlet/>

		</div>
	)
}

export default MainPage
