import classes from './App.module.css'
import { useEffect, useState } from 'react'
import PlayedList from './component/PlayedList'
import io from 'socket.io-client'

const socket = io('http://localhost:3001') 

function App() {
	const [songs, setSongs] = useState([])

	 useEffect(() => {
			// Пример: подписка на обновления плейлиста
			socket.on('updatePlaylist', ({ action, song }) => {
				if (action === 'add') {
					console.log('Song added:', song)
					// Обновите свой локальный стейт с новой песней
				} else if (action === 'remove') {
					console.log('Song removed:', song)
					// Обновите свой локальный стейт, удалив песню с определенным ID
				}
			})

			// Очистка подписки при размонтировании компонента
			return () => {
				socket.off('updatePlaylist')
			}
		}, [])
	const click = item => {
		console.log(item)
	}
	return (
		<div className={classes.container}>
			<div className={classes.player}>
				{songs.length === 0 ? (
					<p></p>
				) : (
					<div className={classes.songTitle}>Now Playing: {songs[0].title}</div>
				)}

				<div className={classes.controls}>
					<button>Previous</button>
					<button>Play</button>
					<button>Next</button>
				</div>
			</div>
			<PlayedList items={songs} click={click} />
		</div>
	)
}

export default App
