import React from 'react'
import classes from './PlayeList.module.css'
import PlayedItem from './PlayedList/PlayedItem/PlayedItem'

const PlayedList = ({ currentPlayed, items, remove, play,onImport }) => {
	const exportPlaylist = () => {
		const jsonData = JSON.stringify(items)

		const blob = new Blob([jsonData], { type: 'application/json' })
		const url = URL.createObjectURL(blob)

		const a = document.createElement('a')
		a.href = url
		a.download = 'Playlist.json'
		document.body.appendChild(a)
		a.click()
		document.body.removeChild(a)
		URL.revokeObjectURL(url)
	}
	return (
		<div className={classes.playlistContainer}>
			<ul className={classes.playlist}>
				{items.length === 0 ? (
					<h1>Нет аудио для проигрыванния</h1>
				) : (
					items.map(item => (
						<PlayedItem
							index={item.id}
							playlistItem={item}
							currentPlayed={currentPlayed}
							remove={remove}
							play={play}
						/>
					))
				)}
			</ul>
			<div>
				<button onClick={() => exportPlaylist()}>Export Playlist</button>
				<button>Import Playlist</button>
			</div>
		</div>
	)
}

export default PlayedList
