import classes from './Music.module.css'
import { useState, useEffect } from 'react'
import PlayedList from './PlayedList/PlayedList'
import { useLocation } from 'react-router-dom'

import MusicPlayer from './MusicPlayer/MusicPlayer'
import { useUpdatePlaylist } from './hooks/useUpdatePlaylist'
import io from 'socket.io-client'
import { useSocketPlayer } from './hooks/useSocketPlayer'

function Music() {
	console.log()
	const location = useLocation()

	const idServer = location.state.idServer || undefined
	const idUser = location.state.idUser || undefined
	//const [socket, setSocket] = useState(null)
	//const [socket, setSocket] = useState(io('http://localhost:3006'))
	const socket = io('http://localhost:3006')
	useEffect(() => {
		//console.log(socket)
		//socket.connect()
		//console.log(socket)
		console.log(idServer)
		console.log(idUser)
		if (idServer !== undefined && idUser !== undefined)
			socket.emit(
				'connectMe',
				{ idServer: idServer, idUser: idUser },
				callback => {
					console.log(callback)
				}
			)

		return () => {
			console.log('TRY DISCONECTING')
			socket?.disconnect()
		}
	}, [idUser])


	const play = item => {
		console.log('PLAYED')
		console.log(item)
		socket?.emit('setPlayItem', { id: idServer, item: item })
	}
	const remove = item => {
		socket?.emit('deltetePlayItem', { id: idServer, item: item })
	}
	const handlerImport = obj => {
		socket?.emit('importedPlaylist', { id: idServer, obj: obj })
	}
	console.log('SIZE NEW ARRAY   ', songs.length)
	const finded = songs.find(x => x.id === currentPlayed)
	const text =
		currentPlayed === -1 ? '' : finded === undefined ? '' : finded.title
	return (
		<div className={classes.container}>
			<div className={classes.info}>
				{finded === undefined ? (
					<h1>Не чего не играет =)</h1>
				) : (
					<div>
						<h1>{text}</h1>
						<p>Песню установил: {finded.сustomer}</p>
						<p>Сылка на песню: {finded.url}</p>
					</div>
				)}
				<MusicPlayer id={idServer} text={text} socket={socket} />
			</div>
			{songs.length === 0 ? (
				<h1>Нет аудио для проигрыванния</h1>
			) : (
				<PlayedList
					remove={remove}
					play={play}
					onImport={handlerImport}
					currentPlayed={currentPlayed}
					items={songs}
				/>
			)}
		</div>
	)
}

export default Music
