import { useEffect, useState } from 'react'

export const useUpdatePlaylist = (socket, serverId) => {
	const [plalistInfo, setPlalistInfo] = useState({
		songs: [],
		currentPlayed: -1,
	})
	useEffect(() => {
		const fetchData = async () => {
			try {
				console.log()
				socket?.emit('getAllInfo')
				socket?.on('updatePlaylist', ({ action, song }) => {
					if (song === undefined) return
					if (action === 'allItems') {
						setPlalistInfo({
							...plalistInfo,
							songs: song,
						})

						socket?.emit('getCurrentPlayed', {
							id: serverId,
							callback: callback => {
								setPlalistInfo({
									...plalistInfo,
									currentPlayed: callback,
								})
							},
						})
					} else if (action === 'newCurrentPlayed') {
						//Хуета подзаброная передлать нахуй
						setPlalistInfo({
							...plalistInfo,
							currentPlayed: song,
						})
					} else if (action === 'add') {
						//Хуета подзаброная передлать нахуй
						setPlalistInfo({
							...plalistInfo,
							songs: [...plalistInfo.songs, song],
						})
					} else if (action === 'remove') {
						//Хуета подзаброная передлать нахуй
						const remove = songs => {
							const updatedSongs = [...songs]

							if (song !== -1) {
								updatedSongs.splice(song, 1)
							}

							return updatedSongs
						}
						setPlalistInfo({
							...plalistInfo,
							songs: remove(plalistInfo.songs),
						})
					}
				})
			} catch (error) {
				console.log('Error fetching data:', error)
			}
		}

		fetchData()
		return () => {
			socket?.off('updatePlaylist')
		}
	}, [plalistInfo, socket, serverId])
	return plalistInfo
}
